import java.util.*;

public class Solution {
    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (s.length() < p.length()) return result;

        int[] pCount = new int[26];
        int[] sCount = new int[26];

        // Frequency count of p
        for (char c : p.toCharArray()) {
            pCount[c - 'a']++;
        }

        int window = p.length();
        for (int i = 0; i < s.length(); i++) {
            // Add current char to sCount
            sCount[s.charAt(i) - 'a']++;

            // Remove leftmost char if window size exceeded
            if (i >= window) {
                sCount[s.charAt(i - window) - 'a']--;
            }

            // If frequency arrays match, it's an anagram
            if (Arrays.equals(sCount, pCount)) {
                result.add(i - window + 1);
            }
        }

        return result;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Input strings s and p
        String s = sc.next();
        String p = sc.next();

        List<Integer> indices = findAnagrams(s, p);

        for (int index : indices) {
            System.out.print(index + " ");
        }
    }
}

