public class Solution {

    public static List<String> addOperators(String num, int target) {
        List<String> res = new ArrayList<>();
        backtrack(res, num, target, "", 0, 0, 0);
        return res;
    }

    private static void backtrack(List<String> res, String num, int target,
                                  String path, int pos, long eval, long prevNum) {
        if (pos == num.length()) {
            if (eval == target) {
                res.add(path);
            }
            return;
        }

        for (int i = pos; i < num.length(); i++) {
            // Skip numbers with leading zero
            if (i != pos && num.charAt(pos) == '0') break;

            String currStr = num.substring(pos, i + 1);
            long currNum = Long.parseLong(currStr);

            if (pos == 0) {
                // First number, no operator
                backtrack(res, num, target, currStr, i + 1, currNum, currNum);
            } else {
                backtrack(res, num, target, path + "+" + currStr, i + 1, eval + currNum, currNum);
                backtrack(res, num, target, path + "-" + currStr, i + 1, eval - currNum, -currNum);
                backtrack(res, num, target, path + "*" + currStr, i + 1,
                        eval - prevNum + prevNum * currNum, prevNum * currNum);
            }
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Read input: num string and target value
        String num = sc.next();
        int target = sc.nextInt();

        List<String> result = addOperators(num, target);
        if (result.isEmpty()) {
            System.out.println("[]");
        } else {
            for (String expr : result) {
                System.out.println(expr);
            }
        }
    }
}

