public class Solution {
    public int calculateMinimumHP(int[][] dungeon) {
        Integer dp[][] = new Integer[dungeon.length][dungeon[0].length];
        return fun(dungeon, 0, 0, dp);
    }

    int fun(int d[][], int i, int j, Integer dp[][]) {
        if (i >= d.length || j >= d[0].length) return Integer.MAX_VALUE;
        if (i == d.length - 1 && j == d[0].length - 1) {
            return dp[i][j] = d[i][j] > 0 ? 1 : 1 - d[i][j];
        }
        if (dp[i][j] != null) return dp[i][j];
        int a1 = fun(d, i + 1, j, dp);
        int a2 = fun(d, i, j + 1, dp);
        int e = Math.min(a1, a2) - d[i][j];
        return dp[i][j] = e > 0 ? e : 1;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Read dimensions
        int m = sc.nextInt(); // number of rows
        int n = sc.nextInt(); // number of columns

        // Read dungeon grid
        int[][] dungeon = new int[m][n];
        for (int i = 0; i < m; i++)
            for (int j = 0; j < n; j++)
                dungeon[i][j] = sc.nextInt();

        // Call the function and print result
        Solution sol = new Solution();
        int result = sol.calculateMinimumHP(dungeon);
        System.out.println(result);
    }
}

