public class Solution {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Read k and w
        int k = sc.nextInt();
        int w = sc.nextInt();

        // Read length of arrays (n)
        int n = sc.nextInt(); // Number of projects

        // Read profits array
        int[] profits = new int[n];
        for (int i = 0; i < n; i++) {
            profits[i] = sc.nextInt();
        }

        // Read capital array
        int[] capital = new int[n];
        for (int i = 0; i < n; i++) {
            capital[i] = sc.nextInt();
        }

        // Compute and print result
        int result = findMaximizedCapital(k, w, profits, capital);
        System.out.println(result);
    }

    public static int findMaximizedCapital(int k, int w, int[] profits, int[] capital) {
        int n = profits.length;
        int[][] projects = new int[n][2];

        for (int i = 0; i < n; i++) {
            projects[i][0] = capital[i];
            projects[i][1] = profits[i];
        }

        // Sort projects by capital
        Arrays.sort(projects, Comparator.comparingInt(a -> a[0]));

        // Max heap for profits
        PriorityQueue<Integer> maxProfitHeap = new PriorityQueue<>(Collections.reverseOrder());

        int i = 0;
        for (int round = 0; round < k; round++) {
            // Push all projects that can be started with current capital
            while (i < n && projects[i][0] <= w) {
                maxProfitHeap.add(projects[i][1]);
                i++;
            }

            if (maxProfitHeap.isEmpty()) break;

            w += maxProfitHeap.poll();
        }

        return w;
    }
}

