public class Solution {
    public static int minRefuelStops(int target, int tank, int[][] stations) {
        PriorityQueue<Integer> pq = new PriorityQueue<>(Collections.reverseOrder());
        int ans = 0, prev = 0;
        for (int[] station : stations) {
            int location = station[0];
            int capacity = station[1];
            tank -= location - prev;

            while (!pq.isEmpty() && tank < 0) {
                tank += pq.poll();
                ans++;
            }

            if (tank < 0) return -1;
            pq.offer(capacity);
            prev = location;
        }

        // Handle final stretch to target
        tank -= target - prev;
        while (!pq.isEmpty() && tank < 0) {
            tank += pq.poll();
            ans++;
        }

        return (tank < 0) ? -1 : ans;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Input: target and startFuel
        int target = sc.nextInt();
        int startFuel = sc.nextInt();

        // Input: number of stations
        int n = sc.nextInt();
        int[][] stations = new int[n][2];

        // Input: station positions and fuels
        for (int i = 0; i < n; i++) {
            stations[i][0] = sc.nextInt(); // position
            stations[i][1] = sc.nextInt(); // fuel
