public class Solution {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Input: n and m
        int n = sc.nextInt();
        int m = sc.nextInt();

        // Input: points array
        int[] points = new int[n];
        for (int i = 0; i < n; i++) {
            points[i] = sc.nextInt();
        }

        // Call and print result
        System.out.println(maxScore(points, m));
    }

    public static long maxScore(int[] points, int m) {
        int n = points.length;
        if (m < n) return 0;

        long left = 1, right = (long) 1e18, ans = 0;

        while (left <= right) {
            long mid = left + (right - left) / 2;
            if (canAchieve(points, n, m, mid)) {
                ans = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }

        return ans;
    }

    private static boolean canAchieve(int[] points, int n, int m, long val) {
        long total = 0, transfer = 0, skipAdd = 0;

        for (int i = 0; i < n && total <= m; i++) {
            int point = points[i];
            long necessary = (val + point - 1) / point;

            if (transfer >= necessary) {
                transfer = 0;
                skipAdd++;
            } else {
                long p = transfer * (long) point;
                long ops = ((val - p) + point - 1) / point;
                total += 2 * ops - 1;
                total += skipAdd;

                transfer = Math.max(ops - 1, 0);
                skipAdd = 0;
            }
        }

        return total <= m;
    }
}

