public class Solution {

    public static int isWinner(int[] player1, int[] player2) {
        int score1 = calculateScore(player1);
        int score2 = calculateScore(player2);

        if (score1 > score2) return 1;
        else if (score2 > score1) return 2;
        else return 0;
    }

    private static int calculateScore(int[] player) {
        int n = player.length;
        int score = 0;

        for (int i = 0; i < n; i++) {
            if ((i >= 1 && player[i - 1] == 10) || (i >= 2 && player[i - 2] == 10)) {
                score += 2 * player[i];
            } else {
                score += player[i];
            }
        }

        return score;
    }

    // Contest-style input/output
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Input for player 1
