public class Solution {

    public static char getKthCharacter(int k) {
        StringBuilder word = new StringBuilder("a");

        while (word.length() < k) {
            StringBuilder next = new StringBuilder();
            for (int i = 0; i < word.length(); i++) {
                next.append(nextChar(word.charAt(i)));
            }
            word.append(next);
        }

        return word.charAt(k - 1);
    }

    // Helper function to get next character in alphabet, with 'z' → 'a'
    private static char nextChar(char c) {
        return (char)((c - 'a' + 1) % 26 + 'a');
    }

    // Main method for input/output
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int k = sc.nextInt();
        sc.close();

        System.out.println(getKthCharacter(k));
    }
}

