class ListNode {
    int val;
    ListNode next;
    ListNode(int x) {
        val = x;
        next = null;
    }
}

public class Solution {

    public static ListNode detectCycle(ListNode head) {
        if (head == null || head.next == null) return null;

        ListNode slow = head, fast = head;

        // Detect cycle
        while (fast != null && fast.next != null) {
            slow = slow.next;
            fast = fast.next.next;

            if (slow == fast) {
                // Find the entry point of the cycle
                slow = head;
                while (slow != fast) {
                    slow = slow.next;
                    fast = fast.next;
                }
                return slow;
            }
        }

        return null; // No cycle
    }

    // Builds linked list from values and connects cycle at `pos`
    public static ListNode buildList(int[] vals, int pos) {
        if (vals.length == 0) return null;

        ListNode head = new ListNode(vals[0]);
        ListNode curr = head;
        ListNode cycleNode = null;

        if (pos == 0) cycleNode = head;

        for (int i = 1; i < vals.length; i++) {
            curr.next = new ListNode(vals[i]);
            curr = curr.next;
            if (i == pos) {
                cycleNode = curr;
            }
        }

        if (pos != -1) {
            curr.next = cycleNode;
        }

        return head;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        // Read linked list values
        String[] parts = sc.nextLine().split(",");
        int[] vals = new int[parts.length];
        for (int i = 0; i < parts.length; i++) {
            vals[i] = Integer.parseInt(parts[i].trim());
        }

        // Read position to create cycle (-1 means no cycle)
        int pos = sc.nextInt();

        // Build the list and detect cycle start
        ListNode head = buildList(vals, pos);
        ListNode cycleStart = detectCycle(head);

        if (cycleStart != null) {
            System.out.println("tail connects to node with value " + cycleStart.val);
        } else {
            System.out.println("no cycle");
        }
    }
}

