public class Solution {

    static final int MOD = 1_000_000_007;

    public static int[] pathsWithMaxScore(List<String> board) {
        int n = board.size();
        int[][] maxScore = new int[n][n];
        int[][] countPaths = new int[n][n];

        // Initialize all as -1 (unreachable)
        for (int[] row : maxScore)
            Arrays.fill(row, -1);

        countPaths[n - 1][n - 1] = 1; // Start from 'S'
        maxScore[n - 1][n - 1] = 0;

        for (int i = n - 1; i >= 0; i--) {
            for (int j = n - 1; j >= 0; j--) {
                if (board.get(i).charAt(j) == 'X' || maxScore[i][j] == -1)
                    continue;

                int currentScore = board.get(i).charAt(j) == 'S' ? 0 :
                                   (board.get(i).charAt(j) == 'E' ? 0 :
                                   board.get(i).charAt(j) - '0');

                // Check all 3 directions (up, left, up-left)
                for (int[] dir : new int[][]{{-1, 0}, {0, -1}, {-1, -1}}) {
                    int ni = i + dir[0], nj = j + dir[1];
                    if (ni < 0 || nj < 0 || board.get(ni).charAt(nj) == 'X')
                        continue;

                    int prevScore = maxScore[i][j] + (board.get(ni).charAt(nj) == 'E' ? 0 :
                                                     (board.get(ni).charAt(nj) == 'S' ? 0 :
                                                     board.get(ni).charAt(nj) - '0'));

                    if (prevScore > maxScore[ni][nj]) {
                        maxScore[ni][nj] = prevScore;
                        countPaths[ni][nj] = countPaths[i][j];
                    } else if (prevScore == maxScore[ni][nj]) {
                        countPaths[ni][nj] = (countPaths[ni][nj] + countPaths[i][j]) % MOD;
                    }
                }
            }
        }

        return maxScore[0][0] == -1 ? new int[]{0, 0} : new int[]{maxScore[0][0], countPaths[0][0]};
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = Integer.parseInt(sc.nextLine());
        List<String> board = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            board.add(sc.nextLine());
        }

        int[] result = pathsWithMaxScore(board);
        System.out.println(result[0] + " " + result[1]);
    }
}

